# Create a 10x10 distance matrix
# For the sake of this example, we'll just use random numbers


sequences = list(
  
  #Ficedula 1-4
  seq1=""
  ,seq2=""
  ,seq3=""
  ,seq4=""
  
  #merops
  ,seq5=""
  ,seq6=""
  ,seq7=""
  #Acanthisitta-chloris
  ,seq8=""
  ,seq9=""
  ,seq10=""
)
freqs = lapply(sequences, function(x) table(strsplit(x, "")))

# Define a distance function based on the frequency of A, G, C, and T
freq_distance = function(seq1, seq2) {
  abs(sum(freqs[[seq1]] - freqs[[seq2]]))
}


dist_matrix = matrix(0, nrow = length(sequences), ncol = length(sequences))
rownames(dist_matrix) = names(freqs)
colnames(dist_matrix) = names(freqs)
for (i in 1:length(sequences)) {
  for (j in i:length(sequences)) {
    dist_matrix[i, j] = freq_distance(rownames(dist_matrix)[i], colnames(dist_matrix)[j])
    dist_matrix[j, i] = dist_matrix[i, j]  # The distance matrix is symmetric
  }
}

distM=dist_matrix

# Ensure the matrix is symmetric
dist_matrix <- (dist_matrix + t(dist_matrix))/2

# Set the diagonal to 0
diag(dist_matrix) <- 0

cluster <- list()
for (i in 1:10) {
  cluster[[i]] <- c(i)
}

# Start the NJ method
for (k in 1:(nrow(dist_matrix)-1)) {
  # Calculate the Q matrix
  n <- nrow(dist_matrix)
  total_dist <- rowSums(dist_matrix)/(n-2)
  Q_matrix <- matrix(nrow=n, ncol=n)
  for (i in 1:(n-1)) {
    for (j in (i+1):n) {
      Q_matrix[i, j] <- (dist_matrix[i, j] - (total_dist[i] + total_dist[j]))
      Q_matrix[j, i] <- Q_matrix[i, j]
    }
  }
  
  # Print the Q matrix
  print(Q_matrix)
  
  # Find the pair of taxa that minimizes the Q matrix
  min_i <- which(Q_matrix == min(Q_matrix[upper.tri(Q_matrix)]), arr.ind=TRUE)[1,]
  
  # Calculate the branch lengths
  total_dist <- rowSums(dist_matrix)
  dist_i <- (1/2)*(dist_matrix[min_i[1], min_i[2]] + (total_dist[min_i[1]] - total_dist[min_i[2]])/(2))
  dist_j <- dist_matrix[min_i[1], min_i[2]] - dist_i
  
  # Update the distance matrix
  new_row <- (1/2)*(dist_matrix[min_i[1],] + dist_matrix[min_i[2],] - dist_matrix[min_i[1], min_i[2]])
  dist_matrix <- rbind(dist_matrix, new_row)
  dist_matrix <- cbind(dist_matrix, c(new_row, 0))
  dist_matrix <- dist_matrix[-min_i, -min_i]
  
  # Print the updated distance matrix
  print(dist_matrix)
  
  # Update the clusters
  cluster[[length(cluster)+1]] <- c(cluster[[min_i[1]]], cluster[[min_i[2]]])
  cluster <- cluster[-min_i]
  
  # Print the updated clusters
  print(cluster,)
